    class Queue:
        def __init__(self):
            self.stack1 = []
            self.stack2 = []

        def enqueue(self, item):
            self.stack1.append(item)

        def dequeue(self):
            if not self.stack1:
                return None
            # Move elements from stack1 to stack2
            while self.stack1:
                self.stack2.append(self.stack1.pop())
            # Now stack2 has all elements
            return self.stack2.pop()

        def peek(self):
            if not self.stack1:
                return None
            # Move elements from stack1 to stack2
            while self.stack1:
                self.stack2.append(self.stack1.pop())
            return self.stack2[-1]

        def is_empty(self):
            return not self.stack1 and not self.stack2

    # Example usage:
